// Enhanced filter and process English articles with comprehensive validation
const articles = $json.data || [];
const processedArticles = [];
const validationErrors = [];

console.log(`Processing ${articles.length} articles from API response`);

for (const article of articles) {
  try {
    // Comprehensive data validation
    if (!article?.id || !article?.attributes) {
      validationErrors.push({
        articleId: article?.id || 'unknown',
        error: 'Missing article ID or attributes',
        severity: 'critical'
      });
      continue;
    }

    const attributes = article.attributes;

    // Validate required fields with detailed logging
    const missingFields = [];
    if (!attributes.title) missingFields.push('title');
    if (!attributes.slug) missingFields.push('slug');
    if (!attributes.blocks && !attributes.content) missingFields.push('blocks/content');

    if (missingFields.length > 0) {
      validationErrors.push({
        articleId: article.id,
        error: `Missing required fields: ${missingFields.join(', ')}`,
        severity: 'critical',
        fields: missingFields
      });
      continue;
    }

    // Enhanced Spanish translation check
    const hasSpanishTranslation = attributes.localizations?.data?.some(
      loc => loc.attributes?.locale === 'es'
    );

    if (hasSpanishTranslation) {
      console.log(`Article ${article.id} already has Spanish translation`);
      continue;
    }

    // Validate and process blocks structure
    const blocks = attributes.blocks || attributes.content || [];
    const validatedBlocks = blocks.map((block, index) => {
      if (!block?.__component) {
        validationErrors.push({
          articleId: article.id,
          blockIndex: index,
          error: 'Block missing __component field',
          severity: 'warning'
        });
        return null;
      }
      return block;
    }).filter(Boolean);

    // Validate relationships
    const relationships = {
      cover: attributes.cover?.data || null,
      author: attributes.author?.data || null,
      category: attributes.category?.data || null
    };

    // Enhanced article processing with all metadata preservation
    const processedArticle = {
      id: article.id,
      title: attributes.title.trim(),
      description: (attributes.description || '').trim(),
      slug: attributes.slug.trim(),
      blocks: validatedBlocks,
      cover: relationships.cover,
      author: relationships.author,
      category: relationships.category,
      publishedAt: attributes.publishedAt,
      createdAt: attributes.createdAt,
      updatedAt: attributes.updatedAt,
      locale: attributes.locale || 'en',
      // Preserve all metadata for reconstruction
      originalAttributes: {
        metaTitle: attributes.metaTitle,
        metaDescription: attributes.metaDescription,
        metaImage: attributes.metaImage,
        article_type: attributes.article_type,
        readingTime: attributes.readingTime,
        featured: attributes.featured,
        tags: attributes.tags
      },
      // Validation metadata
      validationStatus: {
        hasValidBlocks: validatedBlocks.length > 0,
        hasCover: !!relationships.cover,
        hasAuthor: !!relationships.author,
        hasCategory: !!relationships.category,
        blockCount: validatedBlocks.length
      }
    };
    processedArticles.push(processedArticle);

  } catch (error) {
    validationErrors.push({
      articleId: article?.id || 'unknown',
      error: `Processing error: ${error.message}`,
      severity: 'error'
    });
  }
}

// Log processing summary
console.log(`Successfully processed ${processedArticles.length} articles`);
console.log(`Validation errors encountered: ${validationErrors.length}`);

if (validationErrors.length > 0) {
  console.log('Validation errors:', JSON.stringify(validationErrors.slice(0, 10), null, 2));
}

// Return processed articles with validation metadata
return processedArticles.map(article => ({
  json: {
    ...article,
    processingMetadata: {
      processedAt: new Date().toISOString(),
      totalArticlesProcessed: processedArticles.length,
      validationErrors: validationErrors.length
  }
  }
}));

