// Add title and description with enhanced context
translatableContent.push({
  type: 'title',
  text: article.title.trim(),
  context: 'Article title for Yanghua Cable industry website - professional cable manufacturing company',
  maxLength: 120,
  required: true
});

if (article.description && article.description.trim()) {
  translatableContent.push({
  type: 'description',
    text: article.description.trim(),
    context: 'Article meta description for SEO - should be compelling and include key cable industry terms',
    maxLength: 160,
    required: true
  });
}

// Enhanced block processing with comprehensive component support
article.blocks.forEach((block, index) => {
  if (!block || !block.__component) {
    console.warn(`Block ${index} missing __component field, skipping`);
    return;
  }

  const processedBlock = { ...block };

  switch (block.__component) {
    case 'shared.rich-text':
      if (block.body && typeof block.body === 'string' && block.body.trim()) {
        translatableContent.push({
          type: 'rich-text',
          blockIndex: index,
          componentId: block.id,
          text: block.body.trim(),
          context: 'Main article content about cable industry, manufacturing processes, or technical specifications',
          maxLength: 5000
        });
      }
      break;

    case 'shared.quote':
      if (block.title && typeof block.title === 'string' && block.title.trim()) {
        translatableContent.push({
          type: 'quote-title',
          blockIndex: index,
          componentId: block.id,
          text: block.title.trim(),
          context: 'Quote title - professional testimonial or industry insight',
          maxLength: 200
        });
      }
      if (block.body && typeof block.body === 'string' && block.body.trim()) {
        translatableContent.push({
          type: 'quote-body',
          blockIndex: index,
          componentId: block.id,
          text: block.body.trim(),
          context: 'Quote body text - professional statement or industry expertise',
          maxLength: 500
        });
      }
      break;

    case 'shared.media':
      // Handle alt text and captions for images
      if (block.alt && typeof block.alt === 'string' && block.alt.trim()) {
        translatableContent.push({
          type: 'media-alt',
          blockIndex: index,
          componentId: block.id,
          text: block.alt.trim(),
          context: 'Image alt text for accessibility - describe cable products or manufacturing processes',
          maxLength: 125
        });
      }
      if (block.caption && typeof block.caption === 'string' && block.caption.trim()) {
        translatableContent.push({
          type: 'media-caption',
          blockIndex: index,
          componentId: block.id,
          text: block.caption.trim(),
          context: 'Image caption - provide context for cable industry images',
          maxLength: 200
        });
      }
      break;

    case 'shared.slider':
      // Handle slider component text content
      if (block.title && typeof block.title === 'string' && block.title.trim()) {
        translatableContent.push({
          type: 'slider-title',
          blockIndex: index,
          componentId: block.id,
          text: block.title.trim(),
          context: 'Slider title - product showcase or feature highlight',
          maxLength: 100
        });
      }
      if (block.description && typeof block.description === 'string' && block.description.trim()) {
        translatableContent.push({
          type: 'slider-description',
          blockIndex: index,
          componentId: block.id,
          text: block.description.trim(),
          context: 'Slider description - detailed product or service description',
          maxLength: 300
        });
      }
      break;

    default:
      console.log(`Unknown component type: ${block.__component}`);
  }

  processedBlocks.push(processedBlock);
});

