try {
    const translationResponse = $json;
    const originalData = $('Prepare Translation').first().json;
    const article = originalData.originalArticle;

    console.log(`üîÑ Processing translation results for: ${article.title}`);

    let translatedData = null;
    let translationSuccess = false;

    // Parse translation response
    if (translationResponse.choices && translationResponse.choices[0]?.message?.content) {
      const content = translationResponse.choices[0].message.content.trim();

      try {
        translatedData = JSON.parse(content);
        translationSuccess = true;
        console.log('‚úÖ Translation parsed successfully');
      } catch (parseError) {
        console.log('‚ö†Ô∏è Failed to parse JSON, attempting manual extraction...');

        // Fallback: manual extraction
        const titleMatch = content.match(/[\"\']title[\"\']\s*:\s*[\"\']([^\"\']+)[\"\']/i);
        const descMatch = content.match(/[\"\']description[\"\']\s*:\s*[\"\']([^\"\']+)[\"\']/i);

        if (titleMatch || descMatch) {
          translatedData = {
            title: titleMatch ? titleMatch[1] : article.title + ' (ES)',
            description: descMatch ? descMatch[1] : 'Descripci√≥n no disponible',
            blocks: [] // Will be handled below
          };

          // Try to extract blocks
          const blockMatches = content.match(/[\"\']blocks[\"\']\s*:\s*\[(.*?)\]/s);
          if (blockMatches) {
            try {
              const blocksStr = '[' + blockMatches[1] + ']';
              translatedData.blocks = JSON.parse(blocksStr);
            } catch (e) {
              console.log('‚ö†Ô∏è Could not parse blocks array');
            }
          }

          translationSuccess = true;
        }
      }
    }

    if (!translationSuccess) {
      console.log('‚ùå Translation failed, using fallback');
      translatedData = {
        title: article.title + ' (ES)',
        description: article.description ? article.description + ' (Traducci√≥n autom√°tica)' : 'Descripci√≥n no disponible',
        blocks: []
      };
    }

    // Apply translations to blocks - PRESERVE ORIGINAL STRUCTURE
    let translatedBlocks = JSON.parse(JSON.stringify(article.blocks));
    let translationStats = { attempted: 0, successful: 0, failed: 0 };

    // Only translate text-based components
    if (translatedData.blocks && Array.isArray(translatedData.blocks) && article.translatableTexts) {
      article.translatableTexts.forEach((textItem, index) => {
        translationStats.attempted++;

        const targetBlock = translatedBlocks[textItem.blockIndex];
        if (!targetBlock) {
          translationStats.failed++;
          return;
        }

        if (index < translatedData.blocks.length) {
          const translatedText = translatedData.blocks[index];

          if (translatedText && translatedText.trim().length > 0) {
            if (textItem.type === 'rich-text' && targetBlock.__component === 'shared.rich-text') {
              targetBlock.body = translatedText;
              translationStats.successful++;
            } else if (textItem.type === 'quote-title' && targetBlock.__component === 'shared.quote') {
              targetBlock.title = translatedText;
              translationStats.successful++;
            } else if (textItem.type === 'quote-body' && targetBlock.__component === 'shared.quote') {
              targetBlock.body = translatedText;
              translationStats.successful++;
            } else {
              translationStats.failed++;
            }
          } else {
            translationStats.failed++;
          }
        } else {
          translationStats.failed++;
        }
      });
    }

    // Ensure non-text blocks retain their original file references
    translatedBlocks.forEach((block, index) => {
      const originalBlock = article.blocks[index];
      if (!originalBlock) return;

      // Preserve file references for media and slider components
      if (block.__component === 'shared.media') {
        if (originalBlock.file) {
          block.file = originalBlock.file;
        } else {
          // Remove incomplete media blocks
          console.log('‚ö†Ô∏è Removing incomplete media block');
          block._shouldRemove = true;
        }
      }
      if (block.__component === 'shared.slider') {
        if (originalBlock.files && originalBlock.files.length > 0) {
          block.files = originalBlock.files;
        } else {
          // Remove incomplete slider blocks
          console.log('‚ö†Ô∏è Removing incomplete slider block');
          block._shouldRemove = true;
        }
      }
    });

    // Remove incomplete blocks
    translatedBlocks = translatedBlocks.filter(block => !block._shouldRemove);

    // Prepare final Spanish article data
    const spanishArticle = {
      originalId: article.id,
      documentId: article.documentId,
      title: translatedData.title,
      description: translatedData.description,
      slug: article.slug + '-es',
      locale: 'es',
      cover: article.cover ? { id: article.cover.id } : null,
      author: article.author ? { id: article.author.id } : null,
      category: article.category ? { id: article.category.id } : null, // Preserve category
      blocks: translatedBlocks,
      translationStats: translationStats
    };

    console.log(`üìä Translation stats - Attempted: ${translationStats.attempted}, Successful: ${translationStats.successful}, Failed: ${translationStats.failed}`);

    return { json: spanishArticle };
} catch (error) {
    console.error('Error in Process Translation:', error);
    return [{ json: { error: error.message, stack: error.stack } }];
}

