// Enhanced Strapi data preparation with comprehensive field handling
try {
    const inputData = $json;

    console.log(`üì¶ Preparing Strapi payload for: ${inputData.title}`);

    // Validate required fields
    if (!inputData.title || !inputData.slug) {
      throw new Error('Missing required fields: title or slug');
    }

    // Build comprehensive Strapi payload
    const strapiPayload = {
      data: {
        title: inputData.title,
        description: inputData.description || '',
        slug: inputData.slug,
        locale: "es",
        publishedAt: null,

        // Handle media fields
        cover: inputData.cover || null,
        avatar: inputData.avatar || null, // Add avatar field

        // Handle relationship fields
        author: inputData.author || null,
        category: inputData.category || null,

        // Handle blocks with validation
        blocks: Array.isArray(inputData.blocks) ? inputData.blocks.filter(block => {
          // Validate block structure
          if (!block || !block.__component) {
            console.warn('‚ö†Ô∏è Skipping invalid block: missing __component');
            return false;
          }

          // Validate component-specific required fields
          switch (block.__component) {
            case 'shared.rich-text':
              if (!block.body || typeof block.body !== 'string') {
                console.warn('‚ö†Ô∏è Skipping rich-text block: missing body');
                return false;
              }
              break;

            case 'shared.media':
              if (!block.file) {
                console.warn('‚ö†Ô∏è Skipping media block: missing file reference');
                return false;
              }
              break;

            case 'shared.slider':
              if (!block.files || !Array.isArray(block.files) || block.files.length === 0) {
                console.warn('‚ö†Ô∏è Skipping slider block: missing files array');
                return false;
              }
              break;

            case 'shared.quote':
              if (!block.title && !block.body) {
                console.warn('‚ö†Ô∏è Skipping quote block: missing title and body');
                return false;
              }
              break;
          }

          return true;
        }) : []
      }
    };

    // Add metadata if available
    if (inputData.processingMetadata) {
      strapiPayload.data.translationMetadata = inputData.processingMetadata;
    }

    console.log(`‚úÖ Payload prepared with ${strapiPayload.data.blocks.length} valid blocks`);

    // Log field summary
    const fieldSummary = {
      title: !!strapiPayload.data.title,
      description: !!strapiPayload.data.description,
      slug: !!strapiPayload.data.slug,
      cover: !!strapiPayload.data.cover,
      avatar: !!strapiPayload.data.avatar,
      author: !!strapiPayload.data.author,
      category: !!strapiPayload.data.category,
      blocks: strapiPayload.data.blocks.length
    };

    console.log('üìã Field summary:', JSON.stringify(fieldSummary, null, 2));

    return { json: strapiPayload };
} catch (error) {
    console.error('‚ùå Error in Prepare Strapi Data:', error);
    return [{ json: { 
      error: error.message, 
      stack: error.stack,
      context: 'Strapi data preparation failed'
    } }];
}

